package com.study.ladder.example;

public class IMath {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		IMath iMath = new IMath();
		
		int i = 0;
		iMath.prt("i : " + i);
		iMath.prt("i++ : " + i++);
		iMath.prt("i-- : " + i--);
		iMath.prt("++i : " + ++i);
		iMath.prt("--i : " + --i);
	}
	
	public void prt(String s){
		prt(s);
	}

}
